import NavBar from "./NavBar";
import Footer from "./Footer";
import {Link } from "react-router-dom";
import { useState, useEffect } from "react";
import bcrypt from "bcryptjs";
const Zaloguj = () => {

    const data = {
        users: [
          {
            name: "Justyna",
            email: "just@egmail.com",
            password: "123456",
            isAdmin: true,
          },
          {
            name: "Sebastian",
            email: "seb@gmail.com",
            password: bcrypt.hashSync("123456"),
            isAdmin: false,
          },
        ],
    }

    const [valid, setValid] = useState(null);
    const [errors , setErrors] = useState({
        mail: '',
        haslo: '',
    })
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');

    function addFUser() {

       var dataJSON = JSON.stringify(data.users[0])
       var from = dataJSON.search('email":'); 
        var to = dataJSON.search('password'); ;
        var newstr = dataJSON.substring(from,to);
        var str = newstr.split('email":"').join('');
        str = str.split('","').join('');
       //console.log(str)

       var dataJSONPassword = JSON.stringify(data.users[0])
       var from2 = dataJSONPassword.search('password":'); 
        var to2 = dataJSONPassword.search('isAdmin'); ;
        var newstr2 = dataJSONPassword.substring(from2,to2);
        var str2 = newstr2.split('password":"').join('');
        str2 = str2.split('","').join('');
       //console.log(str2)

       if(email.toLowerCase() == str.toLowerCase() && password.toLowerCase() == str2.toLowerCase()){
        console.log("Sucess")
       }
       else {console.log("NOoOOO")}
      }


    const takepassinput = (e) => {
        console.log(e.target.value);
        setPassword(e.target.value);
        console.log(password);

    }

    /*
    useEffect(()=> {
        if(password.length>=4 || password.length === 0){
            setValid({haslo:''})
        }else {
            setErrors({haslo: 'Wymagane 4 litery'});
            console.log(errors.haslo);
        }
    },[password])


    useEffect(()=> {
        if(validateEmail(email)){
            setValid({mail:''})
        }else {
            setErrors({mail:'Niepoprawny email'});
        }
    },[email])
    */

    return (
        <>
        <NavBar />
        <div className="zaloguj">
            <span className="text-zaloguj">Zaloguj</span>
            <label className="label-zaloguj">Login</label>
            <input value={email} onChange={(event) => setEmail(event.target.value)} type="text" placeholder="Wprowadź swój login"></input>
            <div className="invalid-feedback">{errors.mail}</div>
            <label className="label-zaloguj">Hasło</label>
            <input value={password} onChange ={(event) => setPassword(event.target.value.toString())}  type="text" placeholder="Wprowadź swoje hasło"></input>
            <Link to="/profile" className="button-zaloguj" onClick={addFUser}>Zaloguj</Link>
            <span className="info-register">Nie masz jeszcze konta?</span>
            <Link to="/rejestracja" className="button-register">Załóż konto</Link>
        </div>
        <Footer />
        </>
    )
}

export default Zaloguj
