    console.log(props.title)

    const Context = useContext(KoszykContext);

    const addToKoszyk = () => {
        Context.item.push({
            title: props.title,
            price: props.price
        })
    }

    console.log(Context);


    return (



        <
        Link to = "/book" >
        <
        div className = "book" >
