import NavBar from "./NavBar";
import Footer from "./Footer";
import {useContext, useEffect, useState} from "react";
import KoszykContext from "../Context/KoszykContext";

const Koszyk = () => {

    const [isEmptyBin , setisEmptyBin] = useState(false);
    const [items, setItems] = useState([]);

    const context = useContext(KoszykContext);


    useEffect(()=> {
    setItems([...items, {
        title: context.title,
        price: context.price
       }]);
       setisEmptyBin(true);
   },[context.title]);


    console.log(items);
    return(
    <>
    <NavBar />
    <div className="koszyk">
        {isEmptyBin === false ? <span>Koszyk jest pusty</span> : items.map((item,index)=>{
            return(
                <><div className="item-koszyk"><span>{item.title}</span><span>{item.price}</span><button className="btn btn-danger">X</button></div></>
            )
        })}

    </div>
    <Footer />
    </>
    )
}

export default Koszyk
