import NavBar from "./NavBar";
import Footer from "./Footer";
import {Link } from "react-router-dom";
import { useState, useEffect } from "react";
import bcrypt from "bcryptjs";
const Zaloguj = () => {

    const myJSON = '{"name":"Justyna", "email":just@egmail.com", "password":bcrypt.hashSync("123456")';

    const data = {
        users: [
          {
            name: "Justyna",
            email: "just@egmail.com",
            password: bcrypt.hashSync("123456"),
            isAdmin: true,
          },
          {
            name: "Sebastian",
            email: "seb@gmail.com",
            password: bcrypt.hashSync("123456"),
            isAdmin: false,
          },
        ],
    }

    const [valid, setValid] = useState(null);
    const [errors , setErrors] = useState({
        mail: '',
        haslo: '',
    })
    let users = []
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    function addFUserood() {

        var emailValue = email
        /*
        for (let i = 0; i < data.length; i++) {
            if(emailValue == data[i].email){
                console.log("Sucess")
            }
            else{
                console.log("Error")
            }
        }
        */
       var dataJSON = JSON.stringify(Object.assign({}, data))
       console.log(dataJSON.name)
       /*
       for (var i=0;i<data.length;i++) {
            console.log(data[i].email);
        }
        */
      }


    const takepassinput = (e) => {
        console.log(e.target.value);
        setPassword(e.target.value);
        console.log(password);

    }

    const takeemailinput = (e) => {
        console.log(e.target.value);
        /*
        data.forEach((data, index) => {
            if(e.target.value === data.email){
                alert("Skurwie synie")
            }
          });
          */
        setEmail(e.target.value);

        console.log(email);

    }

    useEffect(()=> {
        if(password.length>=4 || password.length === 0){
            setValid({haslo:''})
        }else {
            setErrors({haslo: 'Wymagane 4 litery'});
            console.log(errors.haslo);
        }
    },[password])


    useEffect(()=> {
        if(validateEmail(email)){
            setValid({mail:''})
        }else {
            setErrors({mail:'Niepoprawny email'});
        }
    },[email])

    function validateEmail(email) {
        const res = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return res.test(email);
      }
    return (
        <>
        <NavBar />
        <div className="zaloguj">
            <span className="text-zaloguj">Zaloguj</span>
            <label className="label-zaloguj">Login</label>
            <input value={email} onChange={(event) => setEmail(event.target.value)} /*className={`form-control ${errors.mail ? 'is-invalid' : null }`}*/ type="text" placeholder="Wprowadź swój login"></input>
            <div className="invalid-feedback">{errors.mail}</div>
            <label className="label-zaloguj">Hasło</label>
            <input value={password} onChange ={takepassinput} /*className={`form-control ${errors.haslo ? 'is-invalid' : null}`}*/ type="text" placeholder="Wprowadź swoje hasło"></input>
            <div className="invalid-feedback">{errors.haslo}</div>
            <button className="button-zaloguj" onClick={addFUserood}>Zaloguj</button>
            <span className="info-register">Nie masz jeszcze konta?</span>
            <Link to="/rejestracja" className="button-register">Załóż konto</Link>
        </div>
        <Footer />
        </>
    )
}

export default Zaloguj
