function LoginForm( Login,  error ){

const [details,setDetails]=useState({email:"", password:""});

const submitHandler = e =>{
    e.preventDefault();

    Login(details);
}

    return(
        <div className="LoginForm" onSubmit={submitHandler}>
            <span className="text-zaloguj">Zaloguj</span>
            <label className="label-zaloguj">Login</label>
            <input type="text" placeholder="Wprowadź swój login"></input>
            <label className="label-zaloguj">Hasło</label>
            <input type="text" placeholder="Wprowadź swoje hasło"></input>
            <button className="button-zaloguj">Zaloguj</button>
        </div>
    )
}

export default LoginForm
