import NavBar from "./NavBar";
import Footer from "./Footer";
import {useContext, useEffect, useState} from "react";
import KoszykContext from "../Context/KoszykContext";

const Koszyk = () => {

    const [isEmptyBin , setisEmptyBin] = useState(true);
    const [items, setItems] = useState([]);


    const context = useContext(KoszykContext);







    useEffect(()=>{
        setItems([...items, ...context.item]);
    },[])




    const deleteItem = (index) => {
        let arr = items;
        arr.splice(index,1);
        setItems(arr);
    }









    console.log(items);
    return(
    <>
    <NavBar />
    <div className="koszyk">
        {isEmptyBin === false ? <span>Koszyk jest pusty</span> : <div className="koszyk-item">  {items.map((item,index)=>{

            return(
                <><div className="item-koszyk"><span>{item.title}</span><span>{item.price}</span><button onClick={deleteItem(index)} className="btn btn-danger">X</button></div></>
            )
        })}
        <button className="btn btn-success">Zapłać</button>
        </div>} 
    </div>
    <Footer />
    </>
    )
}

export default Koszyk
