import NavBar from "./NavBar";
import Footer from "./Footer";
import {useContext, useEffect, useState} from "react";
import KoszykContext from "../Context/KoszykContext";

const Koszyk = () => {

    const [isEmptyBin , setisEmptyBin] = useState(true);
    const [items, setItems] = useState([]);

    const context = useContext(KoszykContext);




    useEffect(()=> {
    setItems([...items, ...context.item]);

   },[context]);


   let i;



   const deleteItem = () => {
        items.splice(i,1);
   }




    console.log(items);
    return(
    <>
    <NavBar />
    <div className="koszyk">
        {isEmptyBin === false ? <span>Koszyk jest pusty</span> : items.map((item,index)=>{
             const deleteItem = () => {
                items.splice(index,1);
           }
            return(
                <><div className="item-koszyk"><span>{item.title}</span><span>{item.price}</span><button onClick={deleteItem} className="btn btn-danger">X</button></div></>

            )



        })}

        {isEmptyBin === true ? <button>Zapłać</button> : null}

    </div>
    <Footer />
    </>
    )
}

export default Koszyk
